#include <iostream>

int main() {
    
    std::cout << "The size of int is: " << sizeof(int) << " bytes" << std::endl;
    std::cout << "The size of int is: " << sizeof(long) << " bytes" << std::endl;
    std::cout << "The size of int is: " << sizeof(double) << " bytes" << std::endl;
    std::cout << "The size of int is: " << sizeof(float) << " bytes" << std::endl;
    std::cout << "The size of int is: " << sizeof(char) << " bytes" << std::endl;

    return 0;
}
